window.BILARA = window.BILARA || {};
window.BILARA["dn01"] = window.BILARA["dn01"] || {};
window.BILARA["dn01"]["vi"] = {
   "dn1:0.1": "Trường Bộ Kinh 1",
  "dn1:0.2": "Kinh Phạm Võng",
  "dn1:1.0": "1. Chuyện các du sĩ",
  "dn1:1.1.1": "Tôi nghe như vầy—",
  "dn1:1.1.2": "một thời đức Phật đang đi đường giữa Vương Xá và Nāḷandā, cùng với tăng đoàn Tỳ-kheo đông đảo, khoảng năm trăm Tỳ-kheo.",
  "dn1:1.1.3": "Du sĩ Suppiya cũng đang đi đường giữa Vương Xá và Nāḷandā, cùng với đệ tử là thanh niên Brahmadatta.",
  "dn1:1.1.4": "Tại đó, du sĩ Suppiya dùng nhiều cách để nói xấu đức Phật, nói xấu Pháp, nói xấu tăng đoàn;",
  "dn1:1.1.5": "còn đệ tử của du sĩ Suppiya là thanh niên Brahmadatta thì dùng nhiều cách để tán thán đức Phật, tán thán Pháp, tán thán tăng đoàn.",
  "dn1:1.1.6": "Như vậy, thầy trò ấy nói trái ngược nhau, cứ theo sát phía sau đức Phật và cả tăng đoàn Tỳ-kheo.",
  "dn1:1.2.1": "Rồi đức Phật đến Ambalaṭṭhikā, trú một đêm tại trạm nghỉ của vua, cùng với tăng đoàn Tỳ-kheo.",
  "dn1:1.2.2": "Du sĩ Suppiya cũng đến Ambalaṭṭhikā, trú một đêm tại trạm nghỉ của vua, cùng với đệ tử là thanh niên Brahmadatta.",
  "dn1:1.2.3": "Ở đó cũng vậy, du sĩ Suppiya dùng nhiều cách để nói xấu đức Phật, nói xấu Pháp, nói xấu tăng đoàn;",
  "dn1:1.2.4": "còn đệ tử của ông, thanh niên Brahmadatta, thì dùng nhiều cách để tán thán đức Phật, tán thán Pháp, tán thán tăng đoàn.",
  "dn1:1.2.5": "Như vậy, thầy trò ấy sống với quan điểm trái ngược nhau.",
  "dn1:1.3.1": "Rồi nhiều Tỳ-kheo, vào lúc gần sáng, sau khi thức dậy, ngồi họp ở nhà tròn, và câu chuyện này khởi lên:",
  "dn1:1.3.2": "“Lạ thật, hiền hữu! Kỳ diệu thật, hiền hữu! Mức độ mà đức Phật—bậc biết và thấy, bậc A-la-hán, bậc Chánh Đẳng Giác—hiểu thấu được sự khác biệt về khuynh hướng của các chúng sinh, thật là sâu rộng.",
  "dn1:1.3.3": "Vì du sĩ Suppiya dùng nhiều cách để nói xấu đức Phật, nói xấu Pháp, nói xấu tăng đoàn;",
  "dn1:1.3.4": "còn đệ tử của ông, thanh niên Brahmadatta, thì dùng nhiều cách để tán thán đức Phật, tán thán Pháp, tán thán tăng đoàn.",
  "dn1:1.3.5": "Như vậy, thầy trò ấy nói trái ngược nhau, cứ theo sát phía sau đức Phật và cả tăng đoàn Tỳ-kheo.”",
  "dn1:1.4.1": "Rồi đức Phật, biết được câu chuyện ấy của các Tỳ-kheo, liền đi đến nhà tròn; đến nơi, ngồi xuống chỗ đã soạn sẵn. Ngồi rồi, đức Phật gọi các Tỳ-kheo:",
  "dn1:1.4.2": "“Này các Tỳ-kheo, lúc này các ông đang ngồi họp bàn chuyện gì? Và câu chuyện dang dở của các ông là chuyện gì?”",
  "dn1:1.4.3": "Khi được nói vậy, các Tỳ-kheo bạch đức Phật:",
  "dn1:1.4.4": "“Bạch Thế Tôn, vào lúc gần sáng, sau khi thức dậy, chúng con ngồi họp ở nhà tròn, và câu chuyện này khởi lên:",
  "dn1:1.4.5": "‘Lạ thật, hiền hữu! Kỳ diệu thật, hiền hữu! Mức độ mà đức Phật—bậc biết và thấy, bậc A-la-hán, bậc Chánh Đẳng Giác—hiểu thấu được sự khác biệt về khuynh hướng của các chúng sinh, thật là sâu rộng.",
  "dn1:1.4.6": "Vì du sĩ Suppiya dùng nhiều cách để nói xấu đức Phật, nói xấu Pháp, nói xấu tăng đoàn;",
  "dn1:1.4.7": "còn đệ tử của ông, thanh niên Brahmadatta, thì dùng nhiều cách để tán thán đức Phật, tán thán Pháp, tán thán tăng đoàn.",
  "dn1:1.4.8": "Như vậy, thầy trò ấy nói trái ngược nhau, cứ theo sát phía sau đức Phật và cả tăng đoàn Tỳ-kheo.’",
  "dn1:1.4.9": "Đó là câu chuyện dang dở của chúng con, bạch Thế Tôn; rồi đức Phật đến.”",
  "dn1:1.5.1": "“Này các Tỳ-kheo, nếu người khác nói xấu Ta, hoặc nói xấu Pháp, hoặc nói xấu tăng đoàn, thì các ông không nên khởi sân hận, không nên bực bội, không nên để tâm khó chịu.",
  "dn1:1.5.2": "Này các Tỳ-kheo, nếu người khác nói xấu Ta, hoặc nói xấu Pháp, hoặc nói xấu tăng đoàn, mà các ông khởi giận dữ hay không vừa lòng, thì chính các ông sẽ gặp trở ngại vì điều đó.",
  "dn1:1.5.3": "Này các Tỳ-kheo, nếu người khác nói xấu Ta, hoặc nói xấu Pháp, hoặc nói xấu tăng đoàn, mà các ông giận dữ hay không vừa lòng, thì liệu các ông có thể phân biệt được lời nói hay và lời nói dở của người ta không?”",
  "dn1:1.5.4": "“Bạch Thế Tôn, không.”",
  "dn1:1.6.1": "“Này các Tỳ-kheo, nếu người khác nói xấu Ta, hoặc nói xấu Pháp, hoặc nói xấu tăng đoàn, thì các ông phải gỡ cho rõ điều không đúng là không đúng:",
  "dn1:1.6.2": "‘Điều ấy không đúng, điều ấy không thật; điều ấy không có nơi chúng tôi, điều ấy không được thấy nơi chúng tôi.’",
  "dn1:1.6.3": "Này các Tỳ-kheo, nếu người khác tán thán Ta, hoặc tán thán Pháp, hoặc tán thán tăng đoàn, thì các ông không nên vui mừng, không nên hân hoan, không nên để tâm phấn khích.",
  "dn1:1.6.4": "Này các Tỳ-kheo, nếu người khác tán thán Ta, hoặc tán thán Pháp, hoặc tán thán tăng đoàn, mà các ông vui mừng, hân hoan, phấn khích, thì chính các ông sẽ gặp trở ngại vì điều đó.",
  "dn1:1.6.5": "Này các Tỳ-kheo, nếu người khác tán thán Ta, hoặc tán thán Pháp, hoặc tán thán tăng đoàn, thì các ông phải thừa nhận đúng sự thật là đúng sự thật:",
  "dn1:1.6.6": "‘Điều ấy là thật, điều ấy đúng; điều ấy có nơi chúng tôi, và điều ấy được thấy nơi chúng tôi.’",
  "dn1:1.7.0.1": "2. Giới",
  "dn1:1.7.0.2": "2.1. Tiểu giới",
  "dn1:1.7.1": "Này các Tỳ-kheo, đó chỉ là phần giới nhỏ nhặt, giới hạn hẹp, chỉ thuộc về giới hạnh bề ngoài, nhờ đó người phàm mới có thể tán thán Như Lai.",
  "dn1:1.7.2": "Vậy, này các Tỳ-kheo, thế nào là phần giới nhỏ nhặt, giới hạn hẹp, chỉ thuộc về giới hạnh bề ngoài, nhờ đó người phàm có thể tán thán Như Lai?",
  "dn1:1.8.1": "‘Từ bỏ sát sinh, Sa-môn Gotama sống tránh xa sát sinh, buông bỏ gậy gộc, buông bỏ vũ khí, biết hổ thẹn, có lòng thương xót, sống thương tưởng lợi ích cho mọi loài.’",
  "dn1:1.8.2": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.8.3": "‘Từ bỏ lấy của không cho, Sa-môn Gotama sống tránh xa lấy của không cho, chỉ nhận vật được cho, trông đợi vật được cho, sống thanh tịnh, không trộm cắp.’",
  "dn1:1.8.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.8.5": "‘Từ bỏ hạnh không phạm hạnh, Sa-môn Gotama sống phạm hạnh, sống thanh tịnh, tránh xa hành vi dục của người tại gia.’",
  "dn1:1.8.6": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.9.1": "‘Từ bỏ nói dối, Sa-môn Gotama sống tránh xa nói dối, nói lời chân thật, giữ vững sự thật, đáng tin cậy, không lừa dối đời.’",
  "dn1:1.9.2": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.9.3": "‘Từ bỏ nói hai lưỡi, Sa-môn Gotama không nói lời chia rẽ; nghe ở đây không nói lại nơi kia để gây chia rẽ, nghe nơi kia không nói lại ở đây để gây chia rẽ; sống hòa hợp những người đã chia rẽ, tăng trưởng sự đoàn kết, ưa thích hòa hợp, vui trong hòa hợp, nói lời đưa đến hòa hợp.’",
  "dn1:1.9.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.9.5": "‘Từ bỏ lời thô ác, Sa-môn Gotama nói lời hiền hòa, êm tai, dễ nghe, dễ mến, đi vào lòng người, nhã nhặn, được nhiều người ưa thích.’",
  "dn1:1.9.6": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.9.7": "‘Từ bỏ nói lời phù phiếm, Sa-môn Gotama nói đúng lúc, nói đúng sự thật, nói điều lợi ích, nói về Pháp, nói về giới luật; lời nói có căn cứ, đúng thời, có giới hạn, có ý nghĩa.’",
  "dn1:1.9.8": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.10.1": "‘Sa-môn Gotama tránh xa việc làm hại hạt giống và cây cỏ.’",
  "dn1:1.10.2": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.10.3": "‘Sa-môn Gotama chỉ ăn một bữa trong ngày, không ăn ban đêm, tránh ăn phi thời.’",
  "dn1:1.10.4": "‘Sa-môn Gotama tránh xem múa hát, âm nhạc và các trò giải trí.’",
  "dn1:1.10.5": "‘Sa-môn Gotama tránh dùng vòng hoa, hương liệu, phấn sáp, trang điểm và làm đẹp.’",
  "dn1:1.10.6": "‘Sa-môn Gotama tránh dùng giường cao rộng sang trọng.’",
  "dn1:1.10.7": "‘Sa-môn Gotama tránh nhận vàng bạc.’",
  "dn1:1.10.8": "‘Sa-môn Gotama tránh nhận ngũ cốc chưa nấu.’",
  "dn1:1.10.9": "‘Sa-môn Gotama tránh nhận thịt sống.’",
  "dn1:1.10.10": "‘Sa-môn Gotama tránh nhận phụ nữ và thiếu nữ.’",
  "dn1:1.10.11": "‘Sa-môn Gotama tránh nhận nam nô và nữ nô.’",
  "dn1:1.10.12": "‘Sa-môn Gotama tránh nhận dê và cừu.’",
  "dn1:1.10.13": "‘Sa-môn Gotama tránh nhận gà và heo.’",
  "dn1:1.10.14": "‘Sa-môn Gotama tránh nhận voi, bò, ngựa và ngựa cái.’",
  "dn1:1.10.15": "‘Sa-môn Gotama tránh nhận ruộng đất.’",
  "dn1:1.10.16": "‘Sa-môn Gotama tránh làm sứ giả hay chạy việc.’",
  "dn1:1.10.17": "‘Sa-môn Gotama tránh mua bán.’",
  "dn1:1.10.18": "‘Sa-môn Gotama tránh dùng cân giả, đồ đo giả.’",
  "dn1:1.10.19": "‘Sa-môn Gotama tránh lừa đảo, gian trá.’",
  "dn1:1.10.20": "‘Sa-môn Gotama tránh chém giết, trói buộc, cướp đoạt, bạo lực.’",
  "dn1:1.10.21": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.10.22": "Tiểu giới kết thúc.",
  "dn1:1.11.0": "2.2. Trung giới",
  "dn1:1.11.1": "‘Trong khi có những Sa-môn và Bà-la-môn khác ăn thực phẩm do tín thí cúng dường mà vẫn làm việc gieo trồng các loại hạt giống và cây cỏ,",
  "dn1:1.11.2": "như hạt từ rễ, hạt từ thân, hạt từ cành, hạt từ ngọn, và các loại hạt giống khác,",
  "dn1:1.11.3": "thì Sa-môn Gotama tránh các việc gieo trồng như vậy.’",
  "dn1:1.11.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.12.1": "‘Trong khi có những Sa-môn và Bà-la-môn khác ăn thực phẩm do tín thí cúng dường mà vẫn tích trữ để dùng sau,",
  "dn1:1.12.2": "như tích trữ thức ăn, nước uống, y phục, xe cộ, giường nằm, hương liệu và thực phẩm,",
  "dn1:1.12.3": "thì Sa-môn Gotama tránh tích trữ như vậy.’",
  "dn1:1.12.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.13.1": "‘Trong khi có những Sa-môn và Bà-la-môn khác ăn thực phẩm do tín thí cúng dường mà vẫn tham dự các trò giải trí,",
  "dn1:1.13.2": "như múa hát, nhạc kịch, kể chuyện, đánh trống, đấu voi, đấu ngựa, đấu bò, đấu dê, đấu gà, đấu chim, đánh gậy, đấu tay không, duyệt binh, bày trận…",
  "dn1:1.13.3": "thì Sa-môn Gotama tránh các trò giải trí như vậy.’",
  "dn1:1.13.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.14.1": "‘Trong khi có những Sa-môn và Bà-la-môn khác ăn thực phẩm do tín thí cúng dường mà vẫn đam mê các trò chơi cờ bạc,",
  "dn1:1.14.2": "như các loại bàn cờ, trò ném xúc xắc và nhiều trò chơi khác,",
  "dn1:1.14.3": "thì Sa-môn Gotama tránh các trò cờ bạc như vậy.’",
  "dn1:1.14.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.20.4": "Trung giới kết thúc.",
  "dn1:1.21.0": "2.3. Đại giới",
  "dn1:1.21.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.21.2": "chẳng hạn như: xem chi phần, xem dấu hiệu, xem điềm lạ, giải mộng, xem tướng; đoán theo dấu chuột cắn; tế lửa; tế bằng muỗng; tế bằng trấu; tế bằng cám; tế bằng gạo; tế bằng cơm; tế bằng bơ; tế bằng dầu; tế bằng miệng; tế bằng máu; thuật xem chi thể; thuật xem đồ vật; thuật về vương tộc/chiến trận; thuật về mộ địa; thuật về quỷ thần; thuật bùa chú; thuật rắn; thuật độc; thuật bọ cạp; thuật chuột; thuật chim; thuật quạ; tụng chú nấu/ủ; chú hộ thân; bùa vòng thú (bùa săn)…",
  "dn1:1.21.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.21.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.22.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.22.2": "chẳng hạn như xem tướng: ngọc, vải, gậy, dao/khí giới, kiếm, mũi tên, cung, vũ khí; xem tướng: nữ, nam, trai, gái; xem tướng: nô nam, nô nữ; xem tướng: voi, ngựa, trâu, bò đực giống, bò, dê, cừu, gà, chim cút, kỳ đà, chim diệc/loài chim cổ dài, rùa, nai…",
  "dn1:1.22.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.22.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.23.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.23.2": "chẳng hạn như đoán: vua sẽ xuất chinh; vua sẽ không xuất chinh; quân trong sẽ tiến; quân ngoài sẽ lui; quân ngoài sẽ tiến; quân trong sẽ lui; quân trong sẽ thắng; quân ngoài sẽ thua; quân ngoài sẽ thắng; quân trong sẽ thua; bên này sẽ thắng, bên kia sẽ thua…",
  "dn1:1.23.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.23.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.24.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.24.2": "chẳng hạn như đoán: sẽ có nguyệt thực; sẽ có nhật thực; sẽ có sao bị che khuất; đường đi của mặt trăng mặt trời sẽ đúng quỹ đạo; sẽ lệch quỹ đạo; sao sẽ đi đúng đường; sẽ lệch đường; sao băng sẽ rơi; trời cháy đỏ một vùng; đất rung chuyển; trống trời vang; mặt trăng mặt trời các sao sẽ mọc/lặn, tối/đục, trong/sáng; và dự báo quả báo (điềm báo) của nguyệt thực, nhật thực, sao bị che khuất, các hiện tượng đúng/ lệch quỹ đạo, sao băng, trời cháy đỏ, động đất, trống trời, và việc sáng/tối của mặt trăng mặt trời các sao…",
  "dn1:1.24.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.24.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.25.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.25.2": "chẳng hạn như đoán: mưa thuận sẽ có; mưa trái sẽ có; được mùa sẽ có; mất mùa sẽ có; yên ổn sẽ có; sợ hãi sẽ có; bệnh sẽ có; khỏe mạnh sẽ có; ấn quyết/bùa ấn; tính đếm; toán số; thơ ca; luận thế tục…",
  "dn1:1.25.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.25.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.26.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.26.2": "chẳng hạn như: nghi thức mời gọi; nghi thức tiễn đưa; làm cho đóng; làm cho mở; rải gom; rải tung; làm cho may mắn; làm cho xui rủi; làm cho mang thai trái nghịch; “trói lưỡi”; “khóa hàm”; đọc chú bằng tay; đọc chú bằng hàm; đọc chú bằng tai; hỏi qua gương; hỏi về thiếu nữ; hỏi thần linh; thờ mặt trời; thờ đại thần/đại lực; làm bùng lửa; cầu vinh; cầu may…",
  "dn1:1.26.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.26.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.27.1": "‘Cũng như có những Sa-môn và Bà-la-môn khác, ăn đồ ăn do tín thí cúng dường rồi sống bằng tà mạng nhờ các loại “tà thuật thấp kém”,",
  "dn1:1.27.2": "chẳng hạn như: làm phép “trấn an”; làm phép “giao ước”; làm phép với quỷ thần; làm phép bùa chú; làm phép cầu mưa; làm phép dứt mưa; làm phép cho nhà cửa/đất đai; làm phép “sửa soạn” cho nhà cửa/đất đai; súc miệng; tắm rửa; tế lễ; gây nôn; tẩy xổ; tẩy xổ lên; tẩy xổ xuống; tẩy xổ đầu; nhỏ dầu tai; làm ấm mắt; trị mũi; bôi thuốc mắt; bôi thuốc sau khi bôi; trị bằng kim/que thăm (y thuật mắt); mổ rạch/nhổ (tiểu phẫu); chữa bệnh trẻ nhỏ; cho thuốc rễ; bùa giữ thuốc; “phòng hộ” thuốc men…",
  "dn1:1.27.3": "thì Sa-môn Gotama tránh xa, không sống bằng tà mạng nhờ các loại tà thuật thấp kém như vậy.’",
  "dn1:1.27.4": "Này các Tỳ-kheo, như vậy người phàm có thể tán thán Như Lai.",
  "dn1:1.27.5": "Này các Tỳ-kheo, đó là phần giới nhỏ nhặt, giới hạn hẹp, chỉ thuộc về giới hạnh bề ngoài, nhờ đó người phàm có thể tán thán Như Lai.",
  "dn1:1.27.6": "Đại giới kết thúc.",
  "dn1:1.28.0.1": "3. Kiến",
  "dn1:1.28.0.2": "3.1. Nhóm chấp quá khứ (tưởng luận về thời trước)",
  "dn1:1.28.1": "Này các Tỳ-kheo, còn có những pháp khác sâu xa, khó thấy, khó hiểu, yên lặng, cao quý, không thể nắm bằng suy đoán, tinh tế, bậc trí mới cảm nhận được; những pháp ấy Như Lai tự mình biết rõ, chứng ngộ, rồi tuyên bố; nhờ đó, ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",
  "dn1:1.28.2": "Vậy, này các Tỳ-kheo, những pháp sâu xa, khó thấy, khó hiểu, yên lặng, cao quý, không thể nắm bằng suy đoán, tinh tế, bậc trí mới cảm nhận được; những pháp mà Như Lai tự mình biết rõ, chứng ngộ, rồi tuyên bố; nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng—là những pháp nào?",
  "dn1:1.29.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chấp luận về thời trước, thấy theo thời trước; họ dựa vào quá khứ mà nêu ra nhiều kiểu lập trường, tổng cộng bằng mười tám điểm.",
  "dn1:1.29.2": "Những Sa-môn và Bà-la-môn ấy, dựa vào gì, lấy gì làm điểm tựa, mà trở thành người chấp luận về thời trước, thấy theo thời trước; rồi dựa vào quá khứ mà nêu ra nhiều kiểu lập trường, bằng mười tám điểm?",
  "dn1:1.30.0": "3.1.1. Thuyết thường hằng",
  "dn1:1.30.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương thường hằng; họ nói “tự ngã” và “thế gian” là thường hằng, dựa trên bốn điểm.",
  "dn1:1.30.2": "Những Sa-môn và Bà-la-môn ấy, dựa vào gì, lấy gì làm điểm tựa, mà chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng, dựa trên bốn điểm?",
  "dn1:1.31.1": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn, nhờ tinh cần, nhờ nỗ lực, nhờ chuyên chú, nhờ không buông lung, nhờ khéo để tâm, đạt được một trạng thái định của tâm như vậy; khi tâm đã an trú trong định (…) vị ấy nhớ lại nhiều đời sống trước đây.",
  "dn1:1.31.2": "Tức là nhớ một đời, hai đời, ba đời, bốn đời, năm đời, mười đời, hai mươi đời, ba mươi đời, bốn mươi đời, năm mươi đời, một trăm đời, một ngàn đời, một trăm ngàn đời; nhiều trăm đời, nhiều ngàn đời, nhiều trăm ngàn đời: ‘Ở chỗ kia ta tên như vầy, thuộc họ như vầy, sắc diện như vầy, ăn uống như vầy, cảm thọ vui khổ như vầy, thọ mạng đến mức như vầy; từ đó chết đi, sinh qua chỗ khác; ở chỗ ấy ta tên như vầy… rồi từ đó chết đi, sinh về đây.’ Như vậy, vị ấy nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.31.3": "Vị ấy nói như sau:",
  "dn1:1.31.4": "‘Tự ngã và thế gian là thường hằng, không sinh sản (không tạo mới), vững như cột trụ, đứng yên như chốt trụ;",
  "dn1:1.31.5": "các chúng sinh vẫn chạy tới chạy lui, trôi lăn, chết rồi sinh lại, nhưng vẫn có cái “thường hằng” như vậy.",
  "dn1:1.31.6": "Vì sao?",
  "dn1:1.31.7": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta nhớ lại nhiều đời sống trước.",
  "dn1:1.31.8": "Tức là nhớ một đời… nhiều trăm ngàn đời…: “Ở chỗ kia ta tên như vầy… rồi từ đó chết đi, sinh về đây.” Như vậy, ta nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.31.9": "Nhờ vậy ta biết điều này:",
  "dn1:1.31.10": "“Tự ngã và thế gian là thường hằng…",
  "dn1:1.31.11": "các chúng sinh vẫn trôi lăn, chết rồi sinh lại, nhưng vẫn có cái ‘thường hằng’ như vậy.”’",
  "dn1:1.31.12": "Này các Tỳ-kheo, đó là điểm thứ nhất: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng.",
  "dn1:1.32.1": "Vậy điểm thứ hai là gì, mà dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng?",
  "dn1:1.32.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn… đạt một trạng thái định của tâm như vậy; khi tâm an trú trong định, vị ấy nhớ lại nhiều đời sống trước.",
  "dn1:1.32.3": "Tức là nhớ một lần thế giới co lại–mở ra, hai lần… ba lần… bốn lần… năm lần… mười lần co lại–mở ra: ‘Ở chỗ kia ta tên như vầy… rồi từ đó chết đi, sinh về đây.’ Như vậy, vị ấy nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.32.4": "Vị ấy nói như sau:",
  "dn1:1.32.5": "‘Tự ngã và thế gian là thường hằng…",
  "dn1:1.32.6": "các chúng sinh vẫn trôi lăn… nhưng vẫn có cái “thường hằng” như vậy.",
  "dn1:1.32.7": "Vì sao?",
  "dn1:1.32.8": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta nhớ lại nhiều đời sống trước.",
  "dn1:1.32.9": "Tức là nhớ một lần… mười lần co lại–mở ra…: “Ở chỗ kia ta tên như vầy… rồi từ đó chết đi, sinh về đây.” Như vậy, ta nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.32.10": "Nhờ vậy ta biết điều này:",
  "dn1:1.32.11": "“Tự ngã và thế gian là thường hằng… các chúng sinh vẫn trôi lăn… nhưng vẫn có cái ‘thường hằng’ như vậy.”’",
  "dn1:1.32.12": "Này các Tỳ-kheo, đó là điểm thứ hai: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng.",
  "dn1:1.33.1": "Vậy điểm thứ ba là gì…?",
  "dn1:1.33.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn… đạt định như vậy; khi tâm an trú trong định, vị ấy nhớ lại nhiều đời sống trước.",
  "dn1:1.33.3": "Tức là nhớ mười lần co lại–mở ra, hai mươi lần, ba mươi lần, bốn mươi lần co lại–mở ra… ‘Ở chỗ kia ta tên như vầy… rồi từ đó chết đi, sinh về đây.’ Như vậy, vị ấy nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.33.4": "Vị ấy nói như sau:",
  "dn1:1.33.5": "‘Tự ngã và thế gian là thường hằng…",
  "dn1:1.33.6": "các chúng sinh vẫn trôi lăn… nhưng vẫn có cái “thường hằng” như vậy.",
  "dn1:1.33.7": "Vì sao?",
  "dn1:1.33.8": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta nhớ lại nhiều đời sống trước.",
  "dn1:1.33.9": "Tức là nhớ mười lần… bốn mươi lần co lại–mở ra…: “Ở chỗ kia ta tên như vầy… rồi từ đó chết đi, sinh về đây.” Như vậy, ta nhớ lại nhiều đời sống trước, có cả tướng trạng và chi tiết.",
  "dn1:1.33.10": "Nhờ vậy ta biết điều này:",
  "dn1:1.33.11": "“Tự ngã và thế gian là thường hằng… các chúng sinh vẫn trôi lăn… nhưng vẫn có cái ‘thường hằng’ như vậy.”’",
  "dn1:1.33.12": "Này các Tỳ-kheo, đó là điểm thứ ba: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng.",
  "dn1:1.34.1": "Vậy điểm thứ tư là gì…?",
  "dn1:1.34.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn là người lý luận, hay xét đoán; vị ấy dựa vào suy luận và điều mình tự nghĩ ra mà nói như sau:",
  "dn1:1.34.3": "‘Tự ngã và thế gian là thường hằng, không sinh sản, vững như cột trụ, đứng yên như chốt trụ;",
  "dn1:1.34.4": "các chúng sinh vẫn trôi lăn, chết rồi sinh lại, nhưng vẫn có cái “thường hằng” như vậy.’",
  "dn1:1.34.5": "Này các Tỳ-kheo, đó là điểm thứ tư: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng.",
  "dn1:1.35.1": "Này các Tỳ-kheo, đó là bốn điểm mà nhờ đó các Sa-môn và Bà-la-môn chủ trương thường hằng nói “tự ngã” và “thế gian” là thường hằng.",
  "dn1:1.35.2": "Này các Tỳ-kheo, bất cứ Sa-môn hay Bà-la-môn nào chủ trương thường hằng, nói “tự ngã” và “thế gian” là thường hằng, thì tất cả đều chỉ dựa trên bốn điểm này, hoặc một trong các điểm này; ngoài ra không có gì khác.",
  "dn1:1.36.1": "Này các Tỳ-kheo, Như Lai biết rõ điều ấy:",
  "dn1:1.36.2": "‘Những chỗ chấp kiến này bị nắm giữ như vậy, bị bám chặt như vậy, đưa đến kết cục như vậy, đưa đến đời sau như vậy.’",
  "dn1:1.36.3": "Như Lai biết rõ điều ấy, và còn biết rõ điều vượt lên trên đó; nhưng Như Lai không bám chấp vào cái biết ấy. Do không bám chấp, sự tắt yên được tự mình chứng biết ngay trong hiện tại.",
  "dn1:1.36.4": "Này các Tỳ-kheo, Như Lai là bậc giải thoát không chấp thủ, vì đã biết đúng như thật: sự sinh khởi và sự diệt tận của cảm thọ, vị ngọt của cảm thọ, tai hại của cảm thọ, và lối thoát khỏi cảm thọ.",
  "dn1:1.37.1": "Này các Tỳ-kheo, đó là những pháp sâu xa, khó thấy, khó hiểu, yên lặng, cao quý, không thể nắm bằng suy đoán, tinh tế, bậc trí mới cảm nhận được; những pháp ấy Như Lai tự mình biết rõ, chứng ngộ, rồi tuyên bố; nhờ đó, ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",
  "dn1:1.37.2": "Hết phần tụng đọc thứ nhất.",
  "dn1:2.1.0": "3.1.2. Thuyết bán thường hằng (một phần thường, một phần vô thường)",
  "dn1:2.1.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương “một phần thường hằng, một phần không thường hằng”; họ nói tự ngã và thế gian có phần thường, có phần vô thường, dựa trên bốn điểm.",
  "dn1:2.1.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa, mà chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian, dựa trên bốn điểm?",
  "dn1:2.2.1": "Này các Tỳ-kheo, có lúc—thỉnh thoảng, lâu lâu—sau một thời gian rất dài, thế giới này co rút (hoại diệt).",
  "dn1:2.2.2": "Khi thế giới đang co rút, phần lớn chúng sinh tái sinh vào cõi Quang Âm (Ābhassara).",
  "dn1:2.2.3": "Ở đó họ là do ý tạo, lấy hỷ làm thức ăn, tự phát sáng, đi trong hư không, an trú trong vẻ đẹp, sống rất lâu rất dài.",
  "dn1:2.3.1": "Này các Tỳ-kheo, có lúc—thỉnh thoảng, lâu lâu—sau một thời gian rất dài, thế giới này lại mở ra (thành lập).",
  "dn1:2.3.2": "Khi thế giới mở ra, cung điện Phạm Thiên trống không hiện ra.",
  "dn1:2.3.3": "Rồi có một chúng sinh, do hết thọ hoặc hết phước, từ cõi Quang Âm chết đi, tái sinh vào cung Phạm Thiên trống không ấy.",
  "dn1:2.3.4": "Vị ấy ở đó: do ý tạo, lấy hỷ làm thức ăn, tự phát sáng, đi trong hư không, an trú trong vẻ đẹp, sống rất lâu rất dài.",
  "dn1:2.4.1": "Sống một mình ở đó lâu ngày, nơi vị ấy khởi lên sự chán nản và lo lắng:",
  "dn1:2.4.2": "‘Ước gì còn có những chúng sinh khác đến đây.’",
  "dn1:2.4.3": "Rồi những chúng sinh khác, do hết thọ hoặc hết phước, từ cõi Quang Âm chết đi, tái sinh vào cung Phạm Thiên, làm bạn đồng cư với vị ấy.",
  "dn1:2.4.4": "Họ cũng như vậy: do ý tạo, lấy hỷ làm thức ăn, tự phát sáng, đi trong hư không, an trú trong vẻ đẹp, sống rất lâu rất dài.",
  "dn1:2.5.1": "Ở đó, này các Tỳ-kheo, chúng sinh tái sinh đầu tiên khởi lên ý nghĩ:",
  "dn1:2.5.2": "‘Ta là Phạm Thiên, Đại Phạm Thiên, bậc chế ngự, không ai chế ngự ta; thấy biết mọi điều; có quyền lực; là chúa tể; là đấng tạo tác, đấng sắp đặt; là bậc tối thượng; là người ban lập; là chủ; là cha của những gì đã và sẽ có.’",
  "dn1:2.5.3": "‘Chính ta đã tạo ra các chúng sinh này.’",
  "dn1:2.5.4": "‘Vì sao?’",
  "dn1:2.5.5": "‘Vì trước đây ta đã nghĩ như vầy:",
  "dn1:2.5.6": "“Ước gì còn có những chúng sinh khác đến đây.”",
  "dn1:2.5.7": "Do lời nguyện (ý muốn) của ta, và các chúng sinh này đã đến đây.’",
  "dn1:2.5.8": "Còn những chúng sinh tái sinh sau cũng khởi lên ý nghĩ:",
  "dn1:2.5.9": "‘Vị này là Phạm Thiên, Đại Phạm Thiên… là cha của những gì đã và sẽ có.’",
  "dn1:2.5.10": "‘Chúng ta đã được vị Phạm Thiên này tạo ra.’",
  "dn1:2.5.11": "‘Vì sao?’",
  "dn1:2.5.12": "‘Vì chúng ta đã thấy vị ấy tái sinh ở đây trước, còn chúng ta tái sinh sau.’",
  "dn1:2.6.1": "Ở đó, này các Tỳ-kheo, chúng sinh tái sinh đầu tiên sống lâu hơn, đẹp đẽ hơn, có oai lực hơn.",
  "dn1:2.6.2": "Còn những chúng sinh tái sinh sau thì sống ngắn hơn, kém sắc hơn, ít oai lực hơn.",
  "dn1:2.6.3": "Và có trường hợp, này các Tỳ-kheo, một chúng sinh từ cõi ấy chết đi rồi đến cõi này.",
  "dn1:2.6.4": "Đến cõi này rồi, vị ấy rời nhà, xuất gia.",
  "dn1:2.6.5": "Xuất gia rồi, nhờ tinh cần… đạt một trạng thái định của tâm như vậy; khi tâm an trú trong định, vị ấy nhớ lại đời trước ấy, nhưng không nhớ xa hơn nữa.",
  "dn1:2.6.6": "Vị ấy nói như sau:",
  "dn1:2.6.7": "‘Vị Phạm Thiên ấy—Đại Phạm Thiên… đấng tạo tác… nhờ đó chúng ta được tạo ra—là thường còn, bền chắc, vĩnh hằng, không đổi khác; sẽ tồn tại mãi như vậy.",
  "dn1:2.6.8": "Còn chúng ta—những kẻ được vị Phạm Thiên ấy tạo ra—là vô thường, không bền, thọ mạng ngắn, phải chết, rồi đến đời này.’",
  "dn1:2.6.9": "Này các Tỳ-kheo, đó là điểm thứ nhất: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian.",
  "dn1:2.7.1": "Vậy điểm thứ hai là gì…?",
  "dn1:2.7.2": "Này các Tỳ-kheo, có các vị trời gọi là “hư hỏng vì vui chơi” (khiḍḍāpadosikā); họ sống quá đắm say cười đùa vui chơi. Do quá đắm say cười đùa vui chơi, niệm bị lãng quên. Vì niệm bị lãng quên, các vị trời ấy chết khỏi cõi ấy.",
  "dn1:2.8.1": "Và có trường hợp, này các Tỳ-kheo, một chúng sinh từ cõi ấy chết đi rồi đến cõi này.",
  "dn1:2.8.2": "Đến cõi này rồi, vị ấy rời nhà, xuất gia.",
  "dn1:2.8.3": "Xuất gia rồi, nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, vị ấy nhớ lại đời trước ấy, nhưng không nhớ xa hơn nữa.",
  "dn1:2.9.1": "Vị ấy nói như sau:",
  "dn1:2.9.2": "‘Những vị trời không thuộc loại “hư hỏng vì vui chơi” thì không sống quá đắm say cười đùa vui chơi; do vậy niệm không bị lãng quên. Vì niệm không lãng quên, họ không chết khỏi cõi ấy;",
  "dn1:2.9.3": "họ thường còn, bền chắc, vĩnh hằng, không đổi khác; sẽ tồn tại mãi như vậy.",
  "dn1:2.9.4": "Còn chúng ta trước kia thuộc loại “hư hỏng vì vui chơi”, đã sống quá đắm say cười đùa vui chơi; do vậy niệm bị lãng quên. Vì niệm lãng quên, chúng ta chết khỏi cõi ấy,",
  "dn1:2.9.5": "vô thường, không bền, thọ mạng ngắn, phải chết, rồi đến đời này.’",
  "dn1:2.9.6": "Này các Tỳ-kheo, đó là điểm thứ hai: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian.",
  "dn1:2.10.1": "Vậy điểm thứ ba là gì…?",
  "dn1:2.10.2": "Này các Tỳ-kheo, có các vị trời gọi là “hư hỏng vì tâm” (manopadosikā); họ nhìn chằm chằm nhau quá lâu. Nhìn chằm chằm nhau quá lâu khiến họ làm ô nhiễm tâm nhau. Do tâm bị ô nhiễm, thân mệt mỏi, tâm mệt mỏi; các vị trời ấy chết khỏi cõi ấy.",
  "dn1:2.11.1": "Và có trường hợp, này các Tỳ-kheo, một chúng sinh từ cõi ấy chết đi rồi đến cõi này.",
  "dn1:2.11.2": "Đến cõi này rồi, vị ấy rời nhà, xuất gia.",
  "dn1:2.11.3": "Xuất gia rồi, nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, vị ấy nhớ lại đời trước ấy, nhưng không nhớ xa hơn nữa.",
  "dn1:2.12.1": "Vị ấy nói như sau:",
  "dn1:2.12.2": "‘Những vị trời không thuộc loại “hư hỏng vì tâm” thì không nhìn chằm chằm nhau quá lâu; do vậy họ không làm ô nhiễm tâm nhau. Do tâm không ô nhiễm, thân không mệt, tâm không mệt; họ không chết khỏi cõi ấy,",
  "dn1:2.12.3": "thường còn, bền chắc, vĩnh hằng, không đổi khác; sẽ tồn tại mãi như vậy.",
  "dn1:2.12.4": "Còn chúng ta trước kia thuộc loại “hư hỏng vì tâm”, đã nhìn chằm chằm nhau quá lâu, làm ô nhiễm tâm nhau; do tâm ô nhiễm, thân mệt, tâm mệt. Vì vậy chúng ta chết khỏi cõi ấy,",
  "dn1:2.12.5": "vô thường, không bền, thọ mạng ngắn, phải chết, rồi đến đời này.’",
  "dn1:2.12.6": "Này các Tỳ-kheo, đó là điểm thứ ba: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian.",
  "dn1:2.13.1": "Vậy điểm thứ tư là gì…?",
  "dn1:2.13.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn là người lý luận, hay xét đoán; vị ấy dựa vào suy luận và điều mình tự nghĩ ra mà nói như sau:",
  "dn1:2.13.3": "‘Cái được gọi là mắt, tai, mũi, lưỡi, thân… tự ngã này là vô thường, không bền, không thường, có tính biến hoại.",
  "dn1:2.13.4": "Còn cái được gọi là tâm, là ý, là thức… tự ngã này là thường còn, bền chắc, vĩnh hằng, không đổi khác; sẽ tồn tại mãi như vậy.’",
  "dn1:2.13.5": "Này các Tỳ-kheo, đó là điểm thứ tư: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian.",
  "dn1:2.14.1": "Này các Tỳ-kheo, đó là bốn điểm mà nhờ đó các Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian.",
  "dn1:2.14.2": "Này các Tỳ-kheo, bất cứ Sa-môn hay Bà-la-môn nào chủ trương “một phần thường, một phần vô thường” đối với tự ngã và thế gian, thì tất cả đều chỉ dựa trên bốn điểm này, hoặc một trong các điểm này; ngoài ra không có gì khác.",
  "dn1:2.15.1": "Này các Tỳ-kheo, Như Lai biết rõ điều ấy:",
  "dn1:2.15.2": "‘Những chỗ chấp kiến này bị nắm giữ như vậy, bị bám chặt như vậy, đưa đến kết cục như vậy, đưa đến đời sau như vậy.’",
  "dn1:2.15.3": "Như Lai biết rõ điều ấy, và còn biết rõ điều vượt lên trên đó; nhưng Như Lai không bám chấp vào cái biết ấy. Do không bám chấp, sự tắt yên được tự mình chứng biết ngay trong hiện tại.",
  "dn1:2.15.4": "Này các Tỳ-kheo, Như Lai là bậc giải thoát không chấp thủ, vì đã biết đúng như thật: sự sinh khởi và sự diệt tận của cảm thọ, vị ngọt của cảm thọ, tai hại của cảm thọ, và lối thoát khỏi cảm thọ.",
  "dn1:2.15.5": "Này các Tỳ-kheo, đó là những pháp sâu xa, khó thấy, khó hiểu, yên lặng, cao quý, không thể nắm bằng suy đoán, tinh tế, bậc trí mới cảm nhận được; những pháp ấy Như Lai tự mình biết rõ, chứng ngộ, rồi tuyên bố; nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:2.16.0": "3.1.3. Thuyết hữu hạn–vô hạn (có biên hay không biên)",
  "dn1:2.16.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương “thế gian vừa có biên vừa không biên”; họ nói về tính hữu hạn/vô hạn của thế gian dựa trên bốn điểm.",
  "dn1:2.16.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa, mà chủ trương “hữu hạn–vô hạn” của thế gian dựa trên bốn điểm?",
  "dn1:2.17.1": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn… đạt định như vậy; khi tâm an trú trong định, vị ấy an trú với tưởng “có biên” đối với thế gian.",
  "dn1:2.17.2": "Vị ấy nói như sau:",
  "dn1:2.17.3": "‘Thế gian này là hữu hạn, tròn quanh (như một vòng khép kín).",
  "dn1:2.17.4": "Vì sao?",
  "dn1:2.17.5": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta an trú với tưởng “có biên” đối với thế gian.",
  "dn1:2.17.6": "Nhờ vậy ta biết điều này:",
  "dn1:2.17.7": "“Thế gian này là hữu hạn, tròn quanh.”’",
  "dn1:2.17.8": "Này các Tỳ-kheo, đó là điểm thứ nhất: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “hữu hạn–vô hạn” của thế gian.",
  "dn1:2.18.1": "Vậy điểm thứ hai là gì…?",
  "dn1:2.18.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn… đạt định như vậy; khi tâm an trú trong định, vị ấy an trú với tưởng “vô biên” đối với thế gian.",
  "dn1:2.18.3": "Vị ấy nói như sau:",
  "dn1:2.18.4": "‘Thế gian này là vô hạn, không có biên tế.",
  "dn1:2.18.5": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.18.6": "“Thế gian hữu hạn, tròn quanh”—họ sai.",
  "dn1:2.18.7": "Thế gian là vô hạn, không có biên tế.",
  "dn1:2.18.8": "Vì sao?",
  "dn1:2.18.9": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta an trú với tưởng “vô biên” đối với thế gian.",
  "dn1:2.18.10": "Nhờ vậy ta biết điều này:",
  "dn1:2.18.11": "“Thế gian là vô hạn, không có biên tế.”’",
  "dn1:2.18.12": "Này các Tỳ-kheo, đó là điểm thứ hai: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “hữu hạn–vô hạn” của thế gian.",
  "dn1:2.19.1": "Vậy điểm thứ ba là gì…?",
  "dn1:2.19.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn… đạt định như vậy; khi tâm an trú trong định, vị ấy an trú với tưởng “có biên” theo chiều trên–dưới, nhưng “vô biên” theo chiều ngang.",
  "dn1:2.19.3": "Vị ấy nói như sau:",
  "dn1:2.19.4": "‘Thế gian này vừa hữu hạn vừa vô hạn.",
  "dn1:2.19.5": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.19.6": "“Thế gian hữu hạn, tròn quanh”—họ sai.",
  "dn1:2.19.7": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.19.8": "“Thế gian vô hạn, không có biên tế”—họ cũng sai.",
  "dn1:2.19.9": "Thế gian vừa hữu hạn vừa vô hạn.",
  "dn1:2.19.10": "Vì sao?",
  "dn1:2.19.11": "Vì ta nhờ tinh cần… đạt định như vậy; khi tâm an trú trong định, ta an trú với tưởng “có biên” theo trên–dưới, còn theo ngang là “vô biên”.",
  "dn1:2.19.12": "Nhờ vậy ta biết điều này:",
  "dn1:2.19.13": "“Thế gian vừa hữu hạn vừa vô hạn.”’",
  "dn1:2.19.14": "Này các Tỳ-kheo, đó là điểm thứ ba: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “hữu hạn–vô hạn” của thế gian.",
  "dn1:2.20.1": "Vậy điểm thứ tư là gì…?",
  "dn1:2.20.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn là người lý luận, hay xét đoán; vị ấy dựa vào suy luận và điều mình tự nghĩ ra mà nói như sau:",
  "dn1:2.20.3": "‘Thế gian này không phải hữu hạn, cũng không phải vô hạn.",
  "dn1:2.20.4": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.20.5": "“Thế gian hữu hạn, tròn quanh”—họ sai.",
  "dn1:2.20.6": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.20.7": "“Thế gian vô hạn, không có biên tế”—họ cũng sai.",
  "dn1:2.20.8": "Những Sa-môn và Bà-la-môn nói rằng:",
  "dn1:2.20.9": "“Thế gian vừa hữu hạn vừa vô hạn”—họ cũng sai.",
  "dn1:2.20.10": "Thế gian không phải hữu hạn, cũng không phải vô hạn.’",
  "dn1:2.20.11": "Này các Tỳ-kheo, đó là điểm thứ tư: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương “hữu hạn–vô hạn” của thế gian.",
  "dn1:2.21.1": "Này các Tỳ-kheo, đó là bốn điểm mà nhờ đó các Sa-môn và Bà-la-môn chủ trương “hữu hạn–vô hạn” của thế gian.",
  "dn1:2.21.2": "Này các Tỳ-kheo, bất cứ Sa-môn hay Bà-la-môn nào chủ trương “hữu hạn–vô hạn” của thế gian, thì tất cả đều chỉ dựa trên bốn điểm này, hoặc một trong các điểm này; ngoài ra không có gì khác.",
  "dn1:2.22.1": "Này các Tỳ-kheo, Như Lai biết rõ điều ấy:",
  "dn1:2.22.2": "‘Những chỗ chấp kiến này bị nắm giữ như vậy, bị bám chặt như vậy, đưa đến kết cục như vậy, đưa đến đời sau như vậy.’",
  "dn1:2.22.3": "Như Lai biết rõ điều ấy, và còn biết rõ điều vượt lên trên đó; nhưng Như Lai không bám chấp vào cái biết ấy. Do không bám chấp, sự tắt yên được tự mình chứng biết ngay trong hiện tại.",
  "dn1:2.22.4": "Này các Tỳ-kheo, Như Lai là bậc giải thoát không chấp thủ, vì đã biết đúng như thật: sự sinh khởi và sự diệt tận của cảm thọ, vị ngọt của cảm thọ, tai hại của cảm thọ, và lối thoát khỏi cảm thọ.",
  "dn1:2.22.5": "Này các Tỳ-kheo, đó là những pháp sâu xa, khó thấy, khó hiểu, yên lặng, cao quý, không thể nắm bằng suy đoán, tinh tế, bậc trí mới cảm nhận được; những pháp ấy Như Lai tự mình biết rõ, chứng ngộ, rồi tuyên bố; nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",
 "dn1:2.23.0": "3.1.4. Thuyết lươn lẹo (Amarāvikkhepavāda)",
  "dn1:2.23.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn thuộc loại “lươn lẹo”; bị hỏi câu nào ở đâu cũng rơi vào lối nói quanh co, lươn lẹo, do bốn lý do.",
  "dn1:2.23.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa, mà bị hỏi đâu cũng nói quanh co lươn lẹo, do bốn lý do?",

  "dn1:2.24.1": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn không biết đúng như thật: ‘đây là thiện’, cũng không biết đúng như thật: ‘đây là bất thiện’.",
  "dn1:2.24.2": "Người ấy nghĩ như vầy:",
  "dn1:2.24.3": "‘Ta không biết đúng như thật: “đây là thiện”, cũng không biết đúng như thật: “đây là bất thiện”.",
  "dn1:2.24.4": "Nếu ta, khi không biết đúng như thật như vậy, mà lại trả lời: “đây là thiện” hay “đây là bất thiện”, thì điều đó đối với ta sẽ là nói dối.",
  "dn1:2.24.5": "Mà điều gì là nói dối đối với ta, điều ấy sẽ làm ta bứt rứt, khổ não.",
  "dn1:2.24.6": "Điều gì làm ta bứt rứt, khổ não, điều ấy là chướng ngại đối với ta.’",
  "dn1:2.24.7": "Vì sợ nói dối, ghê sợ nói dối, nên người ấy không dám tuyên bố: ‘đây là thiện’, cũng không dám tuyên bố: ‘đây là bất thiện’; bị hỏi câu nào ở đâu, người ấy liền rơi vào lối nói quanh co, lươn lẹo:",
  "dn1:2.24.8": "‘Tôi không nói là như vậy; tôi cũng không nói là như thế; tôi cũng không nói là khác; tôi cũng không nói là không; tôi cũng không nói là không phải không.’",
  "dn1:2.24.9": "Này các Tỳ-kheo, đó là lý do thứ nhất: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn thuộc loại lươn lẹo; bị hỏi đâu cũng nói quanh co lươn lẹo.",

  "dn1:2.25.1": "Vậy lý do thứ hai là gì…?",
  "dn1:2.25.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn không biết đúng như thật: ‘đây là thiện’, cũng không biết đúng như thật: ‘đây là bất thiện’.",
  "dn1:2.25.3": "Người ấy nghĩ như vầy:",
  "dn1:2.25.4": "‘Ta không biết đúng như thật: “đây là thiện”, cũng không biết đúng như thật: “đây là bất thiện”.",
  "dn1:2.25.5": "Nếu ta, khi không biết đúng như thật như vậy, mà lại trả lời: “đây là thiện” hay “đây là bất thiện”, thì ở đó trong ta có thể khởi lên: ưa thích, tham, sân, chống đối.",
  "dn1:2.25.6": "Chỗ nào trong ta có ưa thích, tham, sân, chống đối, chỗ ấy là chấp thủ của ta.",
  "dn1:2.25.7": "Cái gì là chấp thủ của ta, cái ấy sẽ làm ta bứt rứt, khổ não.",
  "dn1:2.25.8": "Cái gì làm ta bứt rứt, khổ não, cái ấy là chướng ngại đối với ta.’",
  "dn1:2.25.9": "Vì sợ chấp thủ, ghê sợ chấp thủ, nên người ấy không dám tuyên bố: ‘đây là thiện’, cũng không dám tuyên bố: ‘đây là bất thiện’; bị hỏi đâu cũng rơi vào lối nói quanh co, lươn lẹo:",
  "dn1:2.25.10": "‘Tôi không nói là như vậy; tôi cũng không nói là như thế; tôi cũng không nói là khác; tôi cũng không nói là không; tôi cũng không nói là không phải không.’",
  "dn1:2.25.11": "Này các Tỳ-kheo, đó là lý do thứ hai: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn thuộc loại lươn lẹo; bị hỏi đâu cũng nói quanh co lươn lẹo.",

  "dn1:2.26.1": "Vậy lý do thứ ba là gì…?",
  "dn1:2.26.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn không biết đúng như thật: ‘đây là thiện’, cũng không biết đúng như thật: ‘đây là bất thiện’.",
  "dn1:2.26.3": "Người ấy nghĩ như vầy:",
  "dn1:2.26.4": "‘Ta không biết đúng như thật: “đây là thiện”, cũng không biết đúng như thật: “đây là bất thiện”.",
  "dn1:2.26.5": "Nếu ta, khi không biết đúng như thật như vậy, mà lại trả lời: “đây là thiện” hay “đây là bất thiện”,",
  "dn1:2.26.6": "vì có những Sa-môn và Bà-la-môn thông thái, tinh tế, giỏi bẻ luận của người khác, sắc bén như mũi tên; họ có thể đi khắp nơi mà ‘đập vỡ’ các kiến chấp bằng trí luận.",
  "dn1:2.26.7": "Họ sẽ chất vấn ta, vặn hỏi ta, truy hỏi ta.",
  "dn1:2.26.8": "Nếu họ chất vấn ta như vậy, ta sẽ không đáp nổi.",
  "dn1:2.26.9": "Không đáp nổi thì ta sẽ bứt rứt, khổ não.",
  "dn1:2.26.10": "Cái gì làm ta bứt rứt, khổ não, cái ấy là chướng ngại đối với ta.’",
  "dn1:2.26.11": "Vì sợ bị tra vấn, ghê sợ bị tra vấn, nên người ấy không dám tuyên bố: ‘đây là thiện’, cũng không dám tuyên bố: ‘đây là bất thiện’; bị hỏi đâu cũng rơi vào lối nói quanh co, lươn lẹo:",
  "dn1:2.26.12": "‘Tôi không nói là như vậy; tôi cũng không nói là như thế; tôi cũng không nói là khác; tôi cũng không nói là không; tôi cũng không nói là không phải không.’",
  "dn1:2.26.13": "Này các Tỳ-kheo, đó là lý do thứ ba: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn thuộc loại lươn lẹo; bị hỏi đâu cũng nói quanh co lươn lẹo.",

  "dn1:2.27.1": "Vậy lý do thứ tư là gì…?",
  "dn1:2.27.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn đần độn, ngu mờ.",
  "dn1:2.27.3": "Do đần độn, ngu mờ ấy, bị hỏi câu nào ở đâu, người ấy liền rơi vào lối nói quanh co, lươn lẹo:",
  "dn1:2.27.4": "‘Nếu ông hỏi tôi: “Có đời sau không?”—giả sử đối với tôi là “có đời sau”, thì tôi sẽ trả lời ông: “có đời sau”; (nhưng tôi lại nói):",
  "dn1:2.27.5": "“Tôi không nói là như vậy; tôi cũng không nói là như thế; tôi cũng không nói là khác; tôi cũng không nói là không; tôi cũng không nói là không phải không.”",
  "dn1:2.27.6": "‘Không có đời sau…’ (cũng trả lời kiểu ấy)…",
  "dn1:2.27.7": "‘Vừa có vừa không có đời sau…’ (cũng vậy)…",
  "dn1:2.27.8": "‘Không phải có cũng không phải không có đời sau…’ (cũng vậy)…",
  "dn1:2.27.9": "‘Có chúng sinh hóa sinh…’ (cũng vậy)…",
  "dn1:2.27.10": "‘Không có chúng sinh hóa sinh…’ (cũng vậy)…",
  "dn1:2.27.11": "‘Vừa có vừa không có chúng sinh hóa sinh…’ (cũng vậy)…",
  "dn1:2.27.12": "‘Không phải có cũng không phải không có chúng sinh hóa sinh…’ (cũng vậy)…",
  "dn1:2.27.13": "‘Có quả, có dị thục của nghiệp thiện ác…’ (cũng vậy)…",
  "dn1:2.27.14": "‘Không có quả, không có dị thục của nghiệp thiện ác…’ (cũng vậy)…",
  "dn1:2.27.15": "‘Vừa có vừa không có quả, dị thục của nghiệp thiện ác…’ (cũng vậy)…",
  "dn1:2.27.16": "‘Không phải có cũng không phải không có quả, dị thục của nghiệp thiện ác…’ (cũng vậy)…",
  "dn1:2.27.17": "‘Như Lai có tồn tại sau khi chết…’ (cũng vậy)…",
  "dn1:2.27.18": "‘Như Lai không tồn tại sau khi chết…’ (cũng vậy)…",
  "dn1:2.27.19": "‘Như Lai vừa tồn tại vừa không tồn tại sau khi chết…’ (cũng vậy)…",
  "dn1:2.27.20": "‘Như Lai không phải tồn tại cũng không phải không tồn tại sau khi chết?’—nếu ông hỏi tôi như vậy, giả sử đối với tôi là như thế, thì tôi sẽ trả lời ông như thế; (nhưng tôi lại nói):",
  "dn1:2.27.21": "“Tôi không nói là như vậy; tôi cũng không nói là như thế; tôi cũng không nói là khác; tôi cũng không nói là không; tôi cũng không nói là không phải không.”",
  "dn1:2.27.22": "Này các Tỳ-kheo, đó là lý do thứ tư: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn thuộc loại lươn lẹo; bị hỏi đâu cũng nói quanh co lươn lẹo.",

  "dn1:2.28.1": "Này các Tỳ-kheo, đó là bốn lý do mà do đó các Sa-môn và Bà-la-môn thuộc loại lươn lẹo, bị hỏi đâu cũng rơi vào lối nói quanh co lươn lẹo.",
  "dn1:2.28.2": "Này các Tỳ-kheo, bất cứ Sa-môn hay Bà-la-môn nào thuộc loại lươn lẹo, bị hỏi đâu cũng nói quanh co lươn lẹo, thì tất cả đều chỉ do bốn lý do này, hoặc một trong các lý do này; ngoài ra không có gì khác…",
  "dn1:2.29.1": "(…)",
  "dn1:2.29.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",
 "dn1:2.30.0": "3.1.5. Thuyết tự nhiên phát sinh (Adhiccasamuppannavāda)",
  "dn1:2.30.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương “tự nhiên phát sinh”, cho rằng tự ngã và thế gian là tự nhiên phát sinh, dựa trên hai cơ sở.",
  "dn1:2.30.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà chủ trương tự ngã và thế gian là tự nhiên phát sinh, dựa trên hai cơ sở?",

  "dn1:2.31.1": "Này các Tỳ-kheo, có chư thiên gọi là “chúng sinh vô tưởng”.",
  "dn1:2.31.2": "Nhưng khi tưởng khởi lên, các vị trời ấy liền chết khỏi thân đó.",
  "dn1:2.31.3": "Này các Tỳ-kheo, có trường hợp một hữu tình chết khỏi thân ấy rồi đến đời này.",
  "dn1:2.31.4": "Đến đời này rồi, người ấy xuất gia, từ bỏ đời sống tại gia.",
  "dn1:2.31.5": "Sau khi xuất gia, nhờ tinh cần, nỗ lực, chuyên cần, không buông lung, chánh niệm, chánh tác ý, người ấy chứng được một định tâm như vậy; khi tâm đã định, người ấy nhớ lại sự khởi lên của tưởng, nhưng không nhớ gì xa hơn nữa.",
  "dn1:2.31.6": "Người ấy nói như vầy:",
  "dn1:2.31.7": "‘Tự ngã và thế gian là tự nhiên phát sinh.",
  "dn1:2.31.8": "Vì sao vậy?",
  "dn1:2.31.9": "Trước đây tôi không có; nay tôi đang có, từ không mà thành có như vậy.’",
  "dn1:2.31.10": "Này các Tỳ-kheo, đó là cơ sở thứ nhất: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương tự ngã và thế gian là tự nhiên phát sinh.",

  "dn1:2.32.1": "Vậy cơ sở thứ hai là gì…?",
  "dn1:2.32.2": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn là người suy luận, hay xét đoán.",
  "dn1:2.32.3": "Người ấy, bị dẫn dắt bởi suy luận và theo lối xét đoán, tự mình nghĩ ra rồi nói:",
  "dn1:2.32.4": "‘Tự ngã và thế gian là tự nhiên phát sinh.’",
  "dn1:2.32.5": "Này các Tỳ-kheo, đó là cơ sở thứ hai: dựa vào đó, lấy đó làm điểm tựa, có những Sa-môn và Bà-la-môn chủ trương tự ngã và thế gian là tự nhiên phát sinh.",

  "dn1:2.33.1": "Này các Tỳ-kheo, do hai cơ sở này mà những Sa-môn và Bà-la-môn chủ trương “tự nhiên phát sinh” xác lập tự ngã và thế gian là tự nhiên phát sinh.",
  "dn1:2.33.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào chủ trương “tự nhiên phát sinh”, cho rằng tự ngã và thế gian là tự nhiên phát sinh, thì tất cả đều chỉ do hai cơ sở này hoặc một trong hai cơ sở này; ngoài ra không có gì khác…",
  "dn1:2.34.1": "(…)",
  "dn1:2.34.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:2.35.1": "Này các Tỳ-kheo, do mười tám cơ sở ấy mà các Sa-môn và Bà-la-môn thuộc nhóm “chấp quá khứ, nhìn về quá khứ” nêu ra đủ loại lập trường liên quan đến quá khứ.",
  "dn1:2.35.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào thuộc nhóm “chấp quá khứ, nhìn về quá khứ”, nêu ra các lập trường liên quan đến quá khứ, thì tất cả đều chỉ do mười tám cơ sở ấy hoặc một trong các cơ sở ấy; ngoài ra không có gì khác.",

  "dn1:2.36.1": "Và điều này, này các Tỳ-kheo, Như Lai rõ biết:",
  "dn1:2.36.2": "‘Các chỗ chấp kiến này được nắm giữ như vậy, bị nắm chặt như vậy; có khuynh hướng như vậy; có quả báo như vậy.’",
  "dn1:2.36.3": "Như Lai còn biết vượt lên trên điều ấy; nhưng với sự biết ấy, Như Lai không nắm giữ. Do không nắm giữ nên sự tịch tịnh riêng của tự thân đã được Như Lai liễu tri.",
  "dn1:2.36.4": "Do biết đúng như thật sự sinh khởi và đoạn diệt của thọ, vị ngọt, nguy hại, và lối thoát của thọ, Như Lai được giải thoát không chấp thủ, này các Tỳ-kheo.",
  "dn1:2.36.5": "Này các Tỳ-kheo, đó là những pháp sâu xa, khó thấy, khó hiểu, tịch tịnh, thù thắng, không thể với tới bằng suy luận, vi tế, chỉ bậc trí mới cảm nhận được—mà Như Lai tự mình thắng trí, chứng đạt, rồi tuyên thuyết; nhờ đó người tán thán Như Lai đúng như thật sẽ tán thán đúng.",
  "dn1:2.36.6": "Hết phần tụng đọc thứ hai.",

  "dn1:2.37.0": "3.2. Nhóm chấp về tương lai (Aparantakappika)",
  "dn1:2.37.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, nhìn về tương lai; dựa vào tương lai mà nêu ra nhiều loại lập trường khác nhau bằng bốn mươi bốn cơ sở.",
  "dn1:2.37.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà dựa vào tương lai nêu ra nhiều loại lập trường khác nhau bằng bốn mươi bốn cơ sở?",

  "dn1:2.38.0": "3.2.1. Thuyết “có tưởng” (Saññīvāda)",
  "dn1:2.38.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “có tưởng”; họ xác lập tự ngã có tưởng (sau khi chết) bằng mười sáu cơ sở.",
  "dn1:2.38.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà xác lập tự ngã có tưởng bằng mười sáu cơ sở?",
  "dn1:2.38.3": "Họ xác lập: ‘Tự ngã có sắc, lành mạnh, sau khi chết vẫn có tưởng.’",
  "dn1:2.38.4": "Họ xác lập: ‘Tự ngã vô sắc, lành mạnh, sau khi chết vẫn có tưởng.’",
  "dn1:2.38.5": "Họ xác lập: ‘Tự ngã vừa có sắc vừa vô sắc…’",
  "dn1:2.38.6": "Họ xác lập: ‘Tự ngã không phải có sắc cũng không phải vô sắc…’",
  "dn1:2.38.7": "Họ xác lập: ‘Tự ngã hữu hạn…’",
  "dn1:2.38.8": "Họ xác lập: ‘Tự ngã vô hạn…’",
  "dn1:2.38.9": "Họ xác lập: ‘Tự ngã vừa hữu hạn vừa vô hạn…’",
  "dn1:2.38.10": "Họ xác lập: ‘Tự ngã không phải hữu hạn cũng không phải vô hạn…’",
  "dn1:2.38.11": "Họ xác lập: ‘Tự ngã có một loại tưởng…’",
  "dn1:2.38.12": "Họ xác lập: ‘Tự ngã có nhiều loại tưởng…’",
  "dn1:2.38.13": "Họ xác lập: ‘Tự ngã có tưởng hữu hạn…’",
  "dn1:2.38.14": "Họ xác lập: ‘Tự ngã có tưởng vô lượng…’",
  "dn1:2.38.15": "Họ xác lập: ‘Tự ngã thuần lạc…’",
  "dn1:2.38.16": "Họ xác lập: ‘Tự ngã thuần khổ…’",
  "dn1:2.38.17": "Họ xác lập: ‘Tự ngã vừa lạc vừa khổ…’",
  "dn1:2.38.18": "Họ xác lập: ‘Tự ngã không khổ không lạc, lành mạnh, sau khi chết vẫn có tưởng.’",
  "dn1:2.38.19": "Do mười sáu cơ sở ấy mà họ xác lập tự ngã có tưởng.",
  "dn1:2.39.1": "Này các Tỳ-kheo, do mười sáu cơ sở ấy mà các Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “có tưởng”, xác lập tự ngã có tưởng bằng mười sáu cơ sở.",
  "dn1:2.39.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào thuộc nhóm chấp về tương lai, chủ trương “có tưởng”, xác lập tự ngã có tưởng, thì tất cả đều chỉ do mười sáu cơ sở ấy hoặc một trong các cơ sở ấy; ngoài ra không có gì khác…",
  "dn1:2.40.1": "(…)",
  "dn1:2.40.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:3.1.0": "3.2.2. Thuyết “vô tưởng” (Asaññīvāda)",
  "dn1:3.1.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “vô tưởng”; họ xác lập tự ngã vô tưởng (sau khi chết) bằng tám cơ sở.",
  "dn1:3.1.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà xác lập tự ngã vô tưởng bằng tám cơ sở?",
  "dn1:3.2.1": "Họ xác lập: ‘Tự ngã có sắc, lành mạnh, sau khi chết là vô tưởng.’",
  "dn1:3.2.2": "Họ xác lập: ‘Tự ngã vô sắc, lành mạnh, sau khi chết là vô tưởng.’",
  "dn1:3.2.3": "Họ xác lập: ‘Tự ngã vừa có sắc vừa vô sắc…’",
  "dn1:3.2.4": "Họ xác lập: ‘Tự ngã không phải có sắc cũng không phải vô sắc…’",
  "dn1:3.2.5": "Họ xác lập: ‘Tự ngã hữu hạn…’",
  "dn1:3.2.6": "Họ xác lập: ‘Tự ngã vô hạn…’",
  "dn1:3.2.7": "Họ xác lập: ‘Tự ngã vừa hữu hạn vừa vô hạn…’",
  "dn1:3.2.8": "Họ xác lập: ‘Tự ngã không phải hữu hạn cũng không phải vô hạn, lành mạnh, sau khi chết là vô tưởng.’",
  "dn1:3.3.1": "Này các Tỳ-kheo, do tám cơ sở ấy mà các Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “vô tưởng”, xác lập tự ngã vô tưởng bằng tám cơ sở.",
  "dn1:3.3.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào thuộc nhóm chấp về tương lai, chủ trương “vô tưởng”, xác lập tự ngã vô tưởng, thì tất cả đều chỉ do tám cơ sở ấy hoặc một trong các cơ sở ấy; ngoài ra không có gì khác…",
  "dn1:3.4.1": "(…)",
  "dn1:3.4.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:3.5.0": "3.2.3. Thuyết “phi tưởng phi phi tưởng” (Nevasaññīnāsaññīvāda)",
  "dn1:3.5.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “phi tưởng phi phi tưởng”; họ xác lập tự ngã phi tưởng phi phi tưởng (sau khi chết) bằng tám cơ sở.",
  "dn1:3.5.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà xác lập tự ngã phi tưởng phi phi tưởng bằng tám cơ sở?",
  "dn1:3.6.1": "Họ xác lập: ‘Tự ngã có sắc, lành mạnh, sau khi chết là phi tưởng phi phi tưởng.’",
  "dn1:3.6.2": "Họ xác lập: ‘Tự ngã vô sắc…’",
  "dn1:3.6.3": "Họ xác lập: ‘Tự ngã vừa có sắc vừa vô sắc…’",
  "dn1:3.6.4": "Họ xác lập: ‘Tự ngã không phải có sắc cũng không phải vô sắc…’",
  "dn1:3.6.5": "Họ xác lập: ‘Tự ngã hữu hạn…’",
  "dn1:3.6.6": "Họ xác lập: ‘Tự ngã vô hạn…’",
  "dn1:3.6.7": "Họ xác lập: ‘Tự ngã vừa hữu hạn vừa vô hạn…’",
  "dn1:3.6.8": "Họ xác lập: ‘Tự ngã không phải hữu hạn cũng không phải vô hạn, lành mạnh, sau khi chết là phi tưởng phi phi tưởng.’",
  "dn1:3.7.1": "Này các Tỳ-kheo, do tám cơ sở ấy mà các Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, chủ trương “phi tưởng phi phi tưởng”, xác lập tự ngã phi tưởng phi phi tưởng bằng tám cơ sở.",
  "dn1:3.7.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào thuộc nhóm chấp về tương lai, chủ trương “phi tưởng phi phi tưởng”, xác lập tự ngã phi tưởng phi phi tưởng, thì tất cả đều chỉ do tám cơ sở ấy hoặc một trong các cơ sở ấy; ngoài ra không có gì khác…",
  "dn1:3.8.1": "(…)",
  "dn1:3.8.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",
  "dn1:3.9.0": "3.2.4. Thuyết đoạn diệt (Ucchedavāda)",
  "dn1:3.9.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương đoạn diệt, họ xác lập sự đoạn tận, tiêu mất, hoại diệt của một hữu tình đang hiện hữu, dựa trên bảy cơ sở.",
  "dn1:3.9.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà xác lập sự đoạn tận, tiêu mất, hoại diệt của một hữu tình đang hiện hữu, dựa trên bảy cơ sở?",

  "dn1:3.10.1": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn nói như vầy, có kiến giải như vầy:",
  "dn1:3.10.2": "‘Này hiền hữu, tự ngã này có sắc, do bốn đại tạo thành, do cha mẹ sinh ra; khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy được đoạn trừ hoàn toàn.’",
  "dn1:3.10.3": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.11.1": "Người khác nói với người ấy:",
  "dn1:3.11.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không.",
  "dn1:3.11.3": "Nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.11.4": "Có một tự ngã khác: thiên giới, có sắc, thuộc cõi dục, sống bằng thức ăn thô.",
  "dn1:3.11.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.11.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.11.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.11.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.12.1": "Người khác lại nói:",
  "dn1:3.12.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.12.3": "nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.12.4": "Có một tự ngã khác: thiên giới, có sắc, do tâm tạo, đầy đủ các chi phần, các căn không khiếm khuyết.",
  "dn1:3.12.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.12.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.12.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.12.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.13.1": "Người khác lại nói:",
  "dn1:3.13.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.13.3": "nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.13.4": "Có một tự ngã khác: sau khi vượt qua hoàn toàn các tưởng về sắc, với sự tắt mất của các tưởng do xúc chạm, không tác ý đến tưởng sai khác, người ấy chứng “hư không là vô biên”, đạt đến xứ vô biên không.",
  "dn1:3.13.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.13.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.13.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.13.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.14.1": "Người khác lại nói:",
  "dn1:3.14.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.14.3": "nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.14.4": "Có một tự ngã khác: vượt qua xứ vô biên không, chứng “thức là vô biên”, đạt đến xứ vô biên thức.",
  "dn1:3.14.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.14.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.14.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.14.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.15.1": "Người khác lại nói:",
  "dn1:3.15.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.15.3": "nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.15.4": "Có một tự ngã khác: vượt qua xứ vô biên thức, chứng “không có gì”, đạt đến xứ vô sở hữu.",
  "dn1:3.15.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.15.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.15.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.15.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.16.1": "Người khác lại nói:",
  "dn1:3.16.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.16.3": "nhưng tự ngã ấy chưa phải đã bị đoạn trừ hoàn toàn đến mức đó.",
  "dn1:3.16.4": "Có một tự ngã khác: vượt qua xứ vô sở hữu, chứng “thật là tịch tịnh, thật là vi diệu”, đạt đến xứ phi tưởng phi phi tưởng.",
  "dn1:3.16.5": "Ông không biết, không thấy tự ngã ấy.",
  "dn1:3.16.6": "Tôi biết, tôi thấy tự ngã ấy.",
  "dn1:3.16.7": "Tự ngã ấy, khi thân hoại mạng chung thì bị đoạn diệt, tiêu mất, sau khi chết không còn nữa; đến mức này, này hiền hữu, tự ngã ấy mới được đoạn trừ hoàn toàn.’",
  "dn1:3.16.8": "Như vậy, có người xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",

  "dn1:3.17.1": "Này các Tỳ-kheo, do bảy cơ sở này mà các Sa-môn và Bà-la-môn chủ trương đoạn diệt xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu.",
  "dn1:3.17.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào chủ trương đoạn diệt, xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu, thì tất cả đều chỉ do bảy cơ sở này…",

  "dn1:3.18.1": "(…)",
  "dn1:3.18.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:3.19.0": "3.2.5. Thuyết Niết-bàn ngay trong hiện đời (Diṭṭhadhammanibbānavāda)",
  "dn1:3.19.1": "Này các Tỳ-kheo, có những Sa-môn và Bà-la-môn chủ trương Niết-bàn ngay trong hiện đời; họ xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu, dựa trên năm cơ sở.",
  "dn1:3.19.2": "Những Sa-môn và Bà-la-môn ấy dựa vào gì, lấy gì làm điểm tựa mà xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu, dựa trên năm cơ sở?",

  "dn1:3.20.1": "Ở đây, này các Tỳ-kheo, có một Sa-môn hay Bà-la-môn nói như vầy, có kiến giải như vầy:",
  "dn1:3.20.2": "‘Này hiền hữu, khi tự ngã này được đầy đủ, được trang bị, được thỏa hưởng năm dục công đức, thì đến mức đó, này hiền hữu, tự ngã ấy đã đạt Niết-bàn tối thượng ngay trong hiện đời.’",
  "dn1:3.20.3": "Như vậy, có người xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",

  "dn1:3.21.1": "Người khác nói với người ấy:",
  "dn1:3.21.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.21.3": "nhưng tự ngã ấy chưa đạt Niết-bàn tối thượng ngay trong hiện đời chỉ đến mức đó.",
  "dn1:3.21.4": "Vì sao vậy?",
  "dn1:3.21.5": "Các dục, này hiền hữu, vô thường, khổ, có tính đổi khác; do chúng đổi khác và biến hoại nên sầu, bi, khổ, ưu, não khởi lên.",
  "dn1:3.21.6": "Khi tự ngã này lìa dục, lìa các pháp bất thiện, chứng và an trú sơ thiền—có tầm có tứ, hỷ lạc do ly dục sinh—thì đến mức đó, này hiền hữu, tự ngã ấy đạt Niết-bàn tối thượng ngay trong hiện đời.’",
  "dn1:3.21.7": "Như vậy, có người xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",

  "dn1:3.22.1": "Người khác lại nói:",
  "dn1:3.22.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.22.3": "nhưng tự ngã ấy chưa đạt Niết-bàn tối thượng ngay trong hiện đời chỉ đến mức đó.",
  "dn1:3.22.4": "Vì sao vậy?",
  "dn1:3.22.5": "Cái gì còn được suy tầm, còn được xét tứ ở đó, thì cái ấy được gọi là thô.",
  "dn1:3.22.6": "Khi tự ngã này do tầm tứ lắng dịu, nội tĩnh nhất tâm, không tầm không tứ, hỷ lạc do định sinh, chứng và an trú nhị thiền, thì đến mức đó, này hiền hữu, tự ngã ấy đạt Niết-bàn tối thượng ngay trong hiện đời.’",
  "dn1:3.22.7": "Như vậy, có người xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",

  "dn1:3.23.1": "Người khác lại nói:",
  "dn1:3.23.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.23.3": "nhưng tự ngã ấy chưa đạt Niết-bàn tối thượng ngay trong hiện đời chỉ đến mức đó.",
  "dn1:3.23.4": "Vì sao vậy?",
  "dn1:3.23.5": "Cái gì còn thuộc về hỷ, tâm còn phấn khởi ở đó, thì cái ấy được gọi là thô.",
  "dn1:3.23.6": "Khi tự ngã này do ly tham đối với hỷ, an trú xả, chánh niệm tỉnh giác, thân cảm thọ lạc; chứng và an trú tam thiền—về đó các bậc Thánh nói: “xả, có niệm, an trú lạc”—thì đến mức đó, này hiền hữu, tự ngã ấy đạt Niết-bàn tối thượng ngay trong hiện đời.’",
  "dn1:3.23.7": "Như vậy, có người xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",

  "dn1:3.24.1": "Người khác lại nói:",
  "dn1:3.24.2": "‘Này hiền hữu, tự ngã mà ông nói đó có, tôi không nói là không;",
  "dn1:3.24.3": "nhưng tự ngã ấy chưa đạt Niết-bàn tối thượng ngay trong hiện đời chỉ đến mức đó.",
  "dn1:3.24.4": "Vì sao vậy?",
  "dn1:3.24.5": "Cái gì còn là “lạc” được tâm hướng đến ở đó, thì cái ấy được gọi là thô.",
  "dn1:3.24.6": "Khi tự ngã này do từ bỏ lạc và từ bỏ khổ, với sự diệt mất từ trước của hỷ và ưu, chứng và an trú tứ thiền—không khổ không lạc, xả niệm thanh tịnh—thì đến mức đó, này hiền hữu, tự ngã ấy đạt Niết-bàn tối thượng ngay trong hiện đời.’",
  "dn1:3.24.7": "Như vậy, có người xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",

  "dn1:3.25.1": "Này các Tỳ-kheo, do năm cơ sở này mà các Sa-môn và Bà-la-môn chủ trương Niết-bàn ngay trong hiện đời xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu.",
  "dn1:3.25.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào chủ trương Niết-bàn ngay trong hiện đời, xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu, thì tất cả đều chỉ do năm cơ sở này…",

  "dn1:3.26.1": "(…)",
  "dn1:3.26.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:3.27.1": "Này các Tỳ-kheo, do bốn mươi bốn cơ sở ấy mà các Sa-môn và Bà-la-môn thuộc nhóm chấp về tương lai, nhìn về tương lai, dựa vào tương lai nêu ra nhiều loại lập trường khác nhau.",
  "dn1:3.27.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào thuộc nhóm chấp về tương lai, nhìn về tương lai, dựa vào tương lai nêu ra các lập trường khác nhau, thì tất cả đều chỉ do bốn mươi bốn cơ sở ấy…",

  "dn1:3.28.1": "(…)",
  "dn1:3.28.2": "…nhờ đó ai tán thán Như Lai đúng như thật thì sẽ tán thán đúng.",

  "dn1:3.29.1": "Này các Tỳ-kheo, do sáu mươi hai cơ sở ấy mà các Sa-môn và Bà-la-môn: chấp quá khứ, chấp tương lai, hoặc chấp cả quá khứ lẫn tương lai; nhìn về quá khứ, tương lai, hay cả hai; dựa vào quá khứ và tương lai nêu ra đủ loại lập trường khác nhau.",
  "dn1:3.29.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào như vậy nêu ra các lập trường khác nhau về quá khứ và tương lai, thì tất cả đều chỉ do sáu mươi hai cơ sở ấy hoặc một trong các cơ sở ấy; ngoài ra không có gì khác.",

  "dn1:3.30.1": "Và điều này, này các Tỳ-kheo, Như Lai rõ biết:",
  "dn1:3.30.2": "‘Các chỗ chấp kiến này được nắm giữ như vậy, bị nắm chặt như vậy; có khuynh hướng như vậy; có quả báo như vậy.’",
  "dn1:3.30.3": "Như Lai còn biết vượt lên trên điều ấy; nhưng với sự biết ấy, Như Lai không nắm giữ. Do không nắm giữ nên sự tịch tịnh riêng của tự thân đã được Như Lai liễu tri.",
  "dn1:3.30.4": "Do biết đúng như thật sự sinh khởi và đoạn diệt của thọ, vị ngọt, nguy hại, và lối thoát của thọ, Như Lai được giải thoát không chấp thủ, này các Tỳ-kheo.",
  "dn1:3.31.1": "Này các Tỳ-kheo, đó là những pháp sâu xa, khó thấy, khó hiểu, tịch tịnh, thù thắng, không thể với tới bằng suy luận, vi tế, chỉ bậc trí mới cảm nhận được—mà Như Lai tự mình thắng trí, chứng đạt, rồi tuyên thuyết; nhờ đó người tán thán Như Lai đúng như thật sẽ tán thán đúng.",
 "dn1:3.32.0.1": "4. Nền tảng thiết lập “tự ngã và thế giới”",
  "dn1:3.32.0.2": "4.1. Phần “bứt rứt và vùng vẫy”",

  "dn1:3.32.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương thường hằng, xác lập tự ngã và thế giới là thường hằng dựa trên bốn cơ sở—ngay cả điều ấy nơi các vị Sa-môn Bà-la-môn kia, do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.33.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương “một phần thường, một phần không thường”, xác lập tự ngã và thế giới: phần thường, phần không thường dựa trên bốn cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.34.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương hữu hạn/vô hạn, xác lập thế giới là hữu hạn hay vô hạn dựa trên bốn cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.35.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn theo lối “lẩn tránh như con lươn”, bị hỏi chỗ nào cũng rơi vào né tránh lời nói, dựa trên bốn cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.36.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương “tự ngã và thế giới sinh khởi ngẫu nhiên”, xác lập tự ngã và thế giới là ngẫu nhiên sinh khởi dựa trên hai cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.37.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chấp về quá khứ, nhìn theo quá khứ, dựa vào quá khứ nêu ra nhiều loại lập trường khác nhau dựa trên mười tám cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.38.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương có tưởng sau khi chết, xác lập tự ngã có tưởng (sau khi chết) dựa trên mười sáu cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.39.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương không tưởng sau khi chết, xác lập tự ngã không tưởng (sau khi chết) dựa trên tám cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.40.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương phi tưởng phi phi tưởng sau khi chết, xác lập tự ngã phi tưởng phi phi tưởng (sau khi chết) dựa trên tám cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.41.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương đoạn diệt, xác lập sự đoạn tận, tiêu mất, hoại diệt của hữu tình đang hiện hữu dựa trên bảy cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.42.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương “Niết-bàn ngay trong hiện đời”, xác lập “Niết-bàn tối thượng ngay trong hiện đời” cho hữu tình đang hiện hữu dựa trên năm cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.43.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chấp về tương lai, nhìn theo tương lai, dựa vào tương lai nêu ra nhiều loại lập trường khác nhau dựa trên bốn mươi bốn cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",
  "dn1:3.44.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chấp về quá khứ, chấp về tương lai, hoặc chấp cả hai; dựa vào quá khứ và tương lai nêu ra nhiều loại lập trường khác nhau dựa trên sáu mươi hai cơ sở—ngay cả điều ấy… do không biết, không thấy, chỉ là cảm thọ; vì bị ái chi phối nên chỉ là bứt rứt và vùng vẫy mà thôi.",

  "dn1:3.45.0": "4.2. Phần “do xúc làm duyên”",
  "dn1:3.45.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương thường hằng… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.46.1": "Ở đây… chủ trương “một phần thường, một phần không thường”… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.47.1": "Ở đây… chủ trương hữu hạn/vô hạn… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.48.1": "Ở đây… chủ trương “lẩn tránh như con lươn”… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.49.1": "Ở đây… chủ trương “ngẫu nhiên sinh khởi”… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.50.1": "Ở đây… chấp về quá khứ… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.51.1": "Ở đây… chủ trương có tưởng (sau khi chết)… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.52.1": "Ở đây… chủ trương không tưởng (sau khi chết)… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.53.1": "Ở đây… chủ trương phi tưởng phi phi tưởng (sau khi chết)… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.54.1": "Ở đây… chủ trương đoạn diệt… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.55.1": "Ở đây… chủ trương “Niết-bàn ngay trong hiện đời”… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.56.1": "Ở đây… chấp về tương lai… điều ấy cũng là do xúc làm duyên.",
  "dn1:3.57.1": "Ở đây… chấp cả quá khứ lẫn tương lai… điều ấy cũng là do xúc làm duyên.",

  "dn1:3.58.0": "4.3. Phần “không thể có chuyện ấy”",
  "dn1:3.58.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương thường hằng… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.59.1": "Ở đây… chủ trương “một phần thường, một phần không thường”… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.60.1": "Ở đây… chủ trương hữu hạn/vô hạn… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.61.1": "Ở đây… chủ trương “lẩn tránh như con lươn”… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.62.1": "Ở đây… chủ trương “ngẫu nhiên sinh khởi”… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.63.1": "Ở đây… chấp về quá khứ… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.64.1": "Ở đây… chủ trương có tưởng (sau khi chết)… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.65.1": "Ở đây… chủ trương không tưởng (sau khi chết)… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.66.1": "Ở đây… chủ trương phi tưởng phi phi tưởng (sau khi chết)… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.67.1": "Ở đây… chủ trương đoạn diệt… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.68.1": "Ở đây… chủ trương “Niết-bàn ngay trong hiện đời”… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.69.1": "Ở đây… chấp về tương lai… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",
  "dn1:3.70.1": "Ở đây… chấp cả quá khứ lẫn tương lai… mà lại cho rằng sẽ cảm thọ ‘khác ngoài xúc’—chuyện ấy không thể có.",

  "dn1:3.71.0": "4.4. Lời giảng về vòng xoay dựa trên chỗ rơi vào kiến chấp",
  "dn1:3.71.1": "Ở đây, này các Tỳ-kheo, những Sa-môn và Bà-la-môn chủ trương thường hằng…; và những vị chủ trương “một phần thường, một phần không thường”…",
  "dn1:3.71.2": "…và những vị chủ trương hữu hạn/vô hạn…",
  "dn1:3.71.3": "…và những vị theo lối “lẩn tránh như con lươn”…",
  "dn1:3.71.4": "…và những vị chủ trương “ngẫu nhiên sinh khởi”…",
  "dn1:3.71.5": "…và những vị chấp về quá khứ…",
  "dn1:3.71.6": "…và những vị chủ trương có tưởng (sau khi chết)…",
  "dn1:3.71.7": "…và những vị chủ trương không tưởng (sau khi chết)…",
  "dn1:3.71.8": "…và những vị chủ trương phi tưởng phi phi tưởng (sau khi chết)…",
  "dn1:3.71.9": "…và những vị chủ trương đoạn diệt…",
  "dn1:3.71.10": "…và những vị chủ trương “Niết-bàn ngay trong hiện đời”…",
  "dn1:3.71.11": "…và những vị chấp về tương lai…",
  "dn1:3.71.12": "…và những vị chấp cả quá khứ lẫn tương lai, nêu ra nhiều loại lập trường dựa trên sáu mươi hai cơ sở: tất cả đều chạm vào chạm vào sáu xứ xúc; do (các) thọ làm duyên mà có ái; do ái làm duyên mà có thủ; do thủ làm duyên mà có hữu; do hữu làm duyên mà có sinh; do sinh làm duyên mà có già chết, sầu bi khổ ưu não sinh khởi.",

  "dn1:3.72.0": "5. Phần nói về “sự mở ra lối thoát” ",
  "dn1:3.72.1": "Này các Tỳ-kheo, khi một Tỳ-kheo biết đúng như thật sự sinh khởi và đoạn diệt, vị ngọt, nguy hại, và lối thoát của sáu xứ xúc, thì vị ấy biết vượt lên trên tất cả những điều này.",
  "dn1:3.72.2": "Này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào chấp về quá khứ, chấp về tương lai, hoặc chấp cả hai… nêu ra các lập trường khác nhau, tất cả đều bị mắc trong lưới do sáu mươi hai cơ sở này: ở đây họ trồi lên trồi xuống; bị bao trùm trong lưới này, ở đây họ trồi lên trồi xuống.",
  "dn1:3.72.3": "Ví như, này các Tỳ-kheo, một người đánh cá giỏi hay học trò người đánh cá, với tấm lưới mắt nhỏ, phủ lên một ao nước nhỏ. Người ấy nghĩ: ‘Hễ mọi loài sống lớn nào trong ao này, tất cả đều bị mắc trong lưới; ở đây chúng trồi lên trồi xuống; bị bao trùm trong lưới này, ở đây chúng trồi lên trồi xuống.’",
  "dn1:3.72.4": "Cũng vậy, này các Tỳ-kheo, hễ bất cứ Sa-môn hay Bà-la-môn nào… nêu ra các lập trường khác nhau, tất cả đều bị mắc trong lưới do sáu mươi hai cơ sở này; ở đây họ trồi lên trồi xuống; bị bao trùm trong lưới này, ở đây họ trồi lên trồi xuống.",

  "dn1:3.73.1": "Này các Tỳ-kheo, thân của Như Lai còn tồn tại, nhưng sợi dây dẫn đến sinh hữu đã bị cắt đứt",
  "dn1:3.73.2": "Chừng nào thân ấy còn tồn tại, chừng ấy chư thiên và loài người còn thấy Ngài.",
  "dn1:3.73.3": "Khi thân hoại, sau khi mạng sống chấm dứt, chư thiên và loài người không còn thấy Ngài nữa.",
  "dn1:3.73.4": "Ví như, này các Tỳ-kheo, một chùm xoài bị cắt cuống, những quả xoài nào còn dính với cuống, tất cả đều tùy thuộc vào cuống ấy;",
  "dn1:3.73.5": "cũng vậy, này các Tỳ-kheo, thân của Như Lai còn tồn tại, nhưng sợi dây dẫn đến sinh hữu đã bị cắt đứt",
  "dn1:3.73.6": "Chừng nào thân ấy còn tồn tại, chừng ấy chư thiên và loài người còn thấy Ngài;",
  "dn1:3.73.7": "khi thân hoại, sau khi mạng sống chấm dứt, chư thiên và loài người không còn thấy Ngài nữa.’",

  "dn1:3.74.1": "Khi được nói vậy, Tôn giả Ānanda bạch Thế Tôn:",
  "dn1:3.74.2": "‘Bạch Thế Tôn, thật kỳ diệu! thật hy hữu! Pháp môn này tên gọi là gì, bạch Thế Tôn?’",
  "dn1:3.74.3": "‘Vậy này Ānanda, hãy ghi nhớ pháp môn này là: “Lưới nghĩa” cũng được, “Lưới pháp” cũng được, “Lưới Phạm” cũng được, “Lưới kiến” cũng được, và cũng hãy ghi nhớ là “Chiến thắng tối thượng trong trận chiến”.’",
  "dn1:3.74.4": "Thế Tôn nói như vậy.",
  "dn1:3.74.5": "Các Tỳ-kheo hoan hỷ, tán thán lời Thế Tôn dạy.",
  "dn1:3.74.6": "Và khi lời giải thích này đang được thuyết, mười ngàn thế giới rung động.",
  "dn1:3.74.7": "Kinh Phạm Võng (Brahmajāla) – bài kinh thứ nhất – đã kết thúc."
};
window.BILARA["dn01"]["vi"]["dn01:source"] =
  "Dịch từ nguồn Pali https://suttacentral.net/";
